INSERT INTO `quotes` (`id`,`body`,`source`,`contentId`,`random`,`createdOn`,`updatedOn`) VALUES (239,'La guerra è un comportamento con le radici nella cellula singola fluttuante nel mare primordiale. Mangia qualunque cosa, oppure questa mangerà te.','Leto II',78,1,NULL,'2013-02-22 16:25:48');
