INSERT INTO `quotes` (`id`,`body`,`source`,`contentId`,`random`,`createdOn`,`updatedOn`) VALUES (241,'C\'era questo abitante delle terre aride al quale fu chiesto cos\'era più importante, un litro d\'acqua o un\'ampia pozza d\'acqua? L\'abitante delle terre aride ci riflettè su per un attimo e poi rispose: E\' più importante il litro. Nessun singolo individuo oserebbe possedere una grande pozza d\'acqua. Il litro, invece, lo potete nascondere sotto il vostro mantello e scappar via. Nessuno lo saprebbe mai.','',81,0,NULL,'2013-02-27 09:41:53');
INSERT INTO `quotes` (`id`,`body`,`source`,`contentId`,`random`,`createdOn`,`updatedOn`) VALUES (242,'Gli storici esercitano un grande potere, e alcuni di loro lo sanno. Essi ricreano il passato, cambiandolo per adeguarlo alle proprie interpretazioni. Così, cambiano anche il futuro.','',81,1,NULL,'2013-02-22 16:25:48');
INSERT INTO `quotes` (`id`,`body`,`source`,`contentId`,`random`,`createdOn`,`updatedOn`) VALUES (243,'Di rado si vengono a sapere i nomi di coloro che sono davvero ricchi e potenti. Si vedono soltanto i loro portavoce. L\'arena politica produce qualche eccezione, ma questa non rivela per intero la struttura del potere.','Miles Teg',81,1,NULL,'2013-02-22 16:25:48');
INSERT INTO `quotes` (`id`,`body`,`source`,`contentId`,`random`,`createdOn`,`updatedOn`) VALUES (244,'L\'esercito favorisce la tecnologia perchè essa simboleggia la potenza a coloro che non sanno vedere al di là del proprio naso.','Leto II',82,1,NULL,'2013-02-22 16:25:48');
